import { SystemProgram } from "@solana/web3.js";
import { createMultisigAddMemberInstruction, PROGRAM_ID, } from "../generated";
export function multisigAddMember({ multisigPda, configAuthority, rentPayer, newMember, memo, programId = PROGRAM_ID, }) {
    return createMultisigAddMemberInstruction({
        multisig: multisigPda,
        configAuthority,
        rentPayer,
        systemProgram: SystemProgram.programId,
    }, { args: { newMember, memo: memo ?? null } }, programId);
}
