import { createMultisigCreateInstruction, PROGRAM_ID, } from "../generated";
/** @deprecated This instruction is deprecated and will be removed soon. Please use `multisigCreateV2` to ensure future compatibility. */
export function multisigCreate({ creator, multisigPda, configAuthority, threshold, members, timeLock, createKey, rentCollector, memo, programId = PROGRAM_ID, }) {
    return createMultisigCreateInstruction({
        creator,
        createKey,
        multisig: multisigPda,
    }, {
        args: {
            configAuthority,
            threshold,
            members,
            timeLock,
            rentCollector,
            memo: memo ?? null,
        },
    }, programId);
}
