import { createMultisigRemoveSpendingLimitInstruction, PROGRAM_ID, } from "../generated";
export function multisigRemoveSpendingLimit({ multisigPda, configAuthority, spendingLimit, rentCollector, memo, programId = PROGRAM_ID, }) {
    return createMultisigRemoveSpendingLimitInstruction({
        multisig: multisigPda,
        spendingLimit: spendingLimit,
        configAuthority: configAuthority,
        rentCollector: rentCollector,
    }, {
        args: {
            memo: memo ?? null,
        },
    }, programId);
}
