import { getProposalPda } from "../pda";
import { createProposalCancelInstruction, PROGRAM_ID } from "../generated";
export function proposalCancel({ multisigPda, transactionIndex, member, memo, programId = PROGRAM_ID, }) {
    const [proposalPda] = getProposalPda({
        multisigPda,
        transactionIndex,
        programId,
    });
    return createProposalCancelInstruction({ multisig: multisigPda, proposal: proposalPda, member }, { args: { memo: memo ?? null } }, programId);
}
