import { getProposalPda } from "../pda";
import { createProposalRejectInstruction, PROGRAM_ID } from "../generated";
export function proposalReject({ multisigPda, transactionIndex, member, memo, programId = PROGRAM_ID, }) {
    const [proposalPda] = getProposalPda({
        multisigPda,
        transactionIndex,
        programId,
    });
    return createProposalRejectInstruction({ multisig: multisigPda, proposal: proposalPda, member }, { args: { memo: memo ?? null } }, programId);
}
